export const getScoreClass = (score) => {
    if (score >= 8.5)
        return "score-excellent";
    if (score >= 7.0)
        return "score-good";
    if (score >= 5.5)
        return "score-moderate";
    if (score >= 4.0)
        return "score-warning";
    return "score-critical";
};
export const getScoreColor = (score) => {
    if (score >= 8.5)
        return "hsl(142, 71%, 35%)";
    if (score >= 7.0)
        return "hsl(84, 60%, 45%)";
    if (score >= 5.5)
        return "hsl(45, 100%, 50%)";
    if (score >= 4.0)
        return "hsl(30, 100%, 50%)";
    return "hsl(0, 72%, 51%)";
};
